import { useState } from 'react';
import confetti from 'canvas-confetti';
import './index.css';

function launchFireworks() {
  var end = Date.now() + (0.5 * 1000);

  // go Buckeyes!
  var colors = ['#bb0000', '#ffffff'];

  (function frame() {
    confetti({
      particleCount: 2,
      angle: 60,
      spread: 55,
      origin: { x: 0 },
      colors: colors
    });
    confetti({
      particleCount: 2,
      angle: 120,
      spread: 55,
      origin: { x: 1 },
      colors: colors
    });

    if (Date.now() < end) {
      requestAnimationFrame(frame);
    }
  }());
}

function App() {
  const [count, setCount] = useState(0);


  // Add fireworks when clicked !
  return (
    <div >
      <h1 className='select-none text-bold font-heading hover:text-xl' onClick={ () => { 
          launchFireworks();
          setCount(count + 1);
      }}>Click on me !</h1>
      <div className='text-center'>
        <h1 className='text-sm text-center font-heading'>{count}</h1>
      </div>
    </div>
  )
}

export default App;
